        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (40, 'es', '¿Qué elemento químico es el componente principal de las estrellas?', 'Oxígeno', 'Helio', 'Hidrógeno', 'Carbono', 'Hidrógeno', 'El hidrógeno es el combustible principal de las estrellas, donde se fusiona para formar helio.')");
