        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (41, 'es', '¿Quién formuló la teoría de la relatividad?', 'Isaac Newton', 'Albert Einstein', 'Galileo Galilei', 'Stephen Hawking', 'Albert Einstein', 'Albert Einstein es conocido por su teoría de la relatividad, que revolucionó la física.')");
