        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (41, 'pt', 'Quem formulou a teoria da relatividade?', 'Isaac Newton', 'Albert Einstein', 'Galileu Galilei', 'Stephen Hawking', 'Albert Einstein', 'Albert Einstein é conhecido por sua teoria da relatividade, que revolucionou a física.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (42, 'es', '¿Qué es un agujero negro?', 'Una estrella moribunda', 'Una región del espacio con una gravedad tan fuerte que nada puede escapar de ella', 'Un tipo de galaxia', 'Un planeta sin atmósfera', 'Una región del espacio con una gravedad tan fuerte que nada puede escapar de ella', 'Un agujero negro es una región del espacio donde la gravedad es tan intensa que ni siquiera la luz puede escapar.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (42, 'en', 'What is a black hole?', 'A dying star', 'A region of space with gravity so strong that nothing can escape it', 'A type of galaxy', 'A planet without an atmosphere', 'A region of space with gravity so strong that nothing can escape it', 'A black hole is a region of space where gravity is so intense that not even light can escape.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (42, 'pt', 'O que é um buraco negro?', 'Uma estrela moribunda', 'Uma região do espaço com gravidade tão forte que nada pode escapar dela', 'Um tipo de galáxia', 'Um planeta sem atmosfera', 'Uma região do espaço com gravidade tão forte que nada pode escapar dela', 'Um buraco negro é uma região do espaço onde a gravidade é tão intensa que nem mesmo a luz consegue escapar.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (43, 'es', '¿Cuál es el nombre de la partícula subatómica que tiene carga eléctrica positiva?', 'Electrón', 'Neutrón', 'Protón', 'Fotón', 'Protón', 'El protón tiene una carga eléctrica positiva y se encuentra en el núcleo del átomo.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (43, 'en', 'What is the name of the subatomic particle that has a positive electrical charge?', 'Electron', 'Neutron', 'Proton', 'Photon', 'Proton', 'The proton has a positive electrical charge and is found in the nucleus of the atom.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (43, 'pt', 'Qual é o nome da partícula subatômica que tem carga elétrica positiva?', 'Elétron', 'Nêutron', 'Próton', 'Fóton', 'Próton', 'O próton tem carga elétrica positiva e é encontrado no núcleo do átomo.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (44, 'es', '¿Qué principio establece que dos objetos no pueden ocupar el mismo espacio al mismo tiempo?', 'Principio de Arquímedes', 'Principio de incertidumbre de Heisenberg', 'Principio de exclusión de Pauli', 'Principio de conservación de la energía', 'Principio de exclusión de Pauli', 'El Principio de exclusión de Pauli establece que dos fermiones idénticos no pueden ocupar el mismo estado cuántico simultáneamente.')");
        db.execSQL("INSERT INTO PreguntaTraduccion (idPregunta, idioma, pregunta, opcionA, opcionB, opcionC, opcionD, respuestaCorrecta, explicacion) VALUES (44, 'en', 'What principle states that two objects cannot occupy the same space at the same time?', 'Archimedes'' Principle', 'Heisenberg''s Uncertainty Principle', 'Pauli exclusion principle', 'Principle of conservation of energy', 'Pauli exclusion principle', 'The Pauli Exclusion Principle states that two identical fermions cannot occupy the same quantum state simultaneously.')");
